import styled from '@emotion/styled';

export const ErrorContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`;

export const ErrorText = styled.p`
  font-weight: bold;
  font-size: 16px;
  color: red;
  background-color: black;
`;
