import { Component } from 'react';
import { Overlay, ModalSt } from './Modal.styled';

export class Modal extends Component {
  render() {
    const { largeUrl, tags, closeModal, id } = this.props;
    return (
      <Overlay key={id} onClick={() => closeModal()}>
        <ModalSt>
          <img src={largeUrl} alt={tags} />
        </ModalSt>
      </Overlay>
    );
  }
}
