import catCry from '../../image/catCry.jpg';
import { ErrorContainer } from './ImageErrorView.styled';

export const ImageErrorView = ({ message }) => {
  return (
    <ErrorContainer>
      <img src={catCry} width="1000" alt="sadcat" />
      <p>{message}</p>
    </ErrorContainer>
  );
};
